import {Octicon} from '@primer/react'
import {GearIcon} from '@primer/octicons-react'
import {PlatformOsType as OsType} from '../../../types/platform'

export function PlatformIcon({className, osType, size = 16}: {className?: string; osType?: OsType; size?: number}) {
  if (osType === OsType.Linux) {
    return <LinuxIcon className={className} size={size} />
  } else if (osType === OsType.Windows) {
    return <WindowsIcon className={className} size={size} />
  } else {
    return <Octicon icon={GearIcon} className={className} size={size} />
  }
}

