function WindowsIcon({className, size}: {className?: string; size?: number}) {
  return (
    <BaseIconSvg className={className} size={size} altText="Windows icon">
      <path
        data-testid="svg-icon-path"
        fillRule="evenodd"
        d="M6.917 1.662 1.6 2.419V13.53l5.317.74V8.32l-5.314-.037v-.612l5.314-.04v-5.97Zm.742-.106v6.071l7.44-.055V.497L7.66 1.556Zm7.44 6.822-7.44-.052v6.047l7.44 1.034V8.378Z"
      />
    </BaseIconSvg>
  )
}

function BaseIconSvg({
  children,
  className,
  altText,
  size,
}: {
  children: React.ReactNode
  className?: string
  altText?: string
  size?: number
}) {
  return (
    <svg
      data-testid="svg-icon"
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      viewBox="0 0 16 16"
      role="img"
      aria-label={altText}
      className={`octicon ${className}`}
      style={{
        display: 'inline-block',
        userSelect: 'none',
        overflow: 'visible',
      }}
    >
      {children}
    </svg>
  )
}
